import React from 'react';
import './Tentang.css'; // Import the CSS file

const Tentang = () => {
  return (
    <div className="tentang-container">
      <div className="header">
        <h1>ABOUT CHARTIFY</h1>
      </div>
      <div className="content">
        <div className="text-section">
          <h2>Aplikasi Dashboard </h2>
          <p>
            Chartify adalah aplikasi Dashboard Statistik Penjualan yang dirancang untuk menyediakan visualisasi data penjualan yang komprehensif dan interaktif.
          </p>
          <p>
            Aplikasi ini dikembangkan untuk memenuhi penilaian Tugas Besar mata kuliah Mobile Programming Universitas Logistik dan Bisnis Internasional D3TI - 2A.
          </p>
        </div>
        <div className="image-section">
          <img src="logo.png" alt="Chartify Visual" />
        </div>
      </div>
    </div>
  );
};

export default Tentang;
