import React, { useState } from 'react';
import {
  Box,
  CssBaseline,
  Drawer,
  AppBar,
  Toolbar,
  Typography,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  IconButton,
  Container,
  Grid,
  Paper,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import BarChartIcon from '@mui/icons-material/BarChart';
import PieChartIcon from '@mui/icons-material/PieChart';
import ShowChartIcon from '@mui/icons-material/ShowChart';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { purple } from '@mui/material/colors';
import BarChart from './components/BarChart';
import LineChart from './components/LineChart';
import PieChart from './components/PieChart';
import Filter from './components/Filter';

const drawerWidth = 240;

const theme = createTheme({
  palette: {
    primary: {
      main: purple[500],
    },
    secondary: {
      main: purple[200],
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: 'Roboto, sans-serif',
    h4: { fontWeight: 600 },
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: '16px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        },
      },
    },
  },
});

const App = () => {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [filters, setFilters] = useState({ periode: 'monthly', kategori: 'all' });

  const fakeData = {
    bar: {
      labels: ['January', 'February', 'March'],
      datasets: [
        {
          label: 'Sales',
          data: [65, 59, 80],
          backgroundColor: purple[200],
          borderColor: purple[500],
          borderWidth: 1,
        },
      ],
    },
    line: {
      labels: ['January', 'February', 'March'],
      datasets: [
        {
          label: 'Revenue',
          data: [85, 69, 90],
          fill: false,
          backgroundColor: purple[100],
          borderColor: purple[500],
        },
      ],
    },
    pie: {
      labels: ['Electronics', 'Fashion', 'Groceries'],
      datasets: [
        {
          label: 'Category Sales',
          data: [300, 150, 100],
          backgroundColor: [purple[100], purple[300], purple[500]],
          borderColor: [purple[300], purple[400], purple[700]],
          borderWidth: 1,
        },
      ],
    },
  };

  const handleFilterChange = (filterType, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [filterType]: value,
    }));
  };

  const drawer = (
    <div>
      <Box display="flex" alignItems="center" p={2}>
        <img src="/logo.png" alt="Chartify Logo" style={{ height: 40, marginRight: 8 }} />
        <Typography variant="h6" fontWeight="bold" color="primary">
          Chartify
        </Typography>
      </Box>
      <Divider />
      <List>
        <ListItem button>
          <ListItemIcon>
            <BarChartIcon />
          </ListItemIcon>
          <ListItemText primary="Grafik Penjualan" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <ShowChartIcon />
          </ListItemIcon>
          <ListItemText primary="Grafik Pendapatan" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <PieChartIcon />
          </ListItemIcon>
          <ListItemText primary="Distribusi Kategori" />
        </ListItem>
      </List>
    </div>
  );

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />

        {/* AppBar */}
        <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
          <Toolbar>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={() => setMobileOpen(!mobileOpen)}
              sx={{ mr: 2, display: { md: 'none' } }}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" noWrap component="div">
              Dashboard Statistik Penjualan
            </Typography>
          </Toolbar>
        </AppBar>

        {/* Sidebar Drawer */}
        <Drawer
          variant="permanent"
          sx={{
            display: { xs: 'none', md: 'block' },
            width: drawerWidth,
            flexShrink: 0,
            [`& .MuiDrawer-paper`]: {
              width: drawerWidth,
              boxSizing: 'border-box',
            },
          }}
          open
        >
          {drawer}
        </Drawer>

        {/* Mobile Drawer */}
        <Drawer
          variant="temporary"
          open={mobileOpen}
          onClose={() => setMobileOpen(false)}
          ModalProps={{ keepMounted: true }}
          sx={{
            display: { xs: 'block', md: 'none' },
            [`& .MuiDrawer-paper`]: { width: drawerWidth },
          }}
        >
          {drawer}
        </Drawer>

        {/* Main Content */}
        <Box
          component="main"
          sx={{
            flexGrow: 1,
            p: 3,
            width: { md: `calc(100% - ${drawerWidth}px)` },
            mt: 8,
          }}
        >
          <Container maxWidth="lg">
            <Box mb={3}>
              <Filter onFilterChange={handleFilterChange} />
            </Box>

            <Grid container spacing={3}>
              <Grid item xs={12} sm={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" color="primary" gutterBottom>
                    Grafik Penjualan
                  </Typography>
                  <BarChart data={fakeData.bar} />
                </Paper>
              </Grid>
              <Grid item xs={12} sm={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" color="primary" gutterBottom>
                    Grafik Pendapatan
                  </Typography>
                  <LineChart data={fakeData.line} />
                </Paper>
              </Grid>
              <Grid item xs={12}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" color="primary" gutterBottom>
                    Distribusi Kategori Penjualan
                  </Typography>
                  <PieChart data={fakeData.pie} />
                </Paper>
              </Grid>
            </Grid>
          </Container>
        </Box>
      </Box>
    </ThemeProvider>
  );
};

export default App;
