import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Box,
  CssBaseline,
  Container,
  Grid,
  Paper,
  useMediaQuery
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { purple } from '@mui/material/colors';
import BarChart from './components/BarChart';
import LineChart from './components/LineChart';
import PieChart from './components/PieChart';
import Filter from './components/Filter';
import { useTheme } from '@mui/material/styles';

const customTheme = createTheme({
  palette: {
    primary: {
      main: purple[500],
    },
    secondary: {
      main: purple[200],
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: 'Roboto, sans-serif',
    h4: {
      fontWeight: 600,
    },
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: '16px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        },
      },
    },
  },
});

const App = () => {
  const [filters, setFilters] = useState({ periode: 'monthly', kategori: 'all' });
  const [drawerOpen, setDrawerOpen] = useState(false);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };

  const menuItems = ['Dashboard', 'Laporan', 'Pengaturan'];

  const drawer = (
    <Box sx={{ width: 250 }} onClick={handleDrawerToggle}>
      <List>
        {menuItems.map((text) => (
          <ListItem button key={text}>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  const fakeData = {
    bar: {
      labels: ['January', 'February', 'March'],
      datasets: [
        {
          label: 'Sales',
          data: [65, 59, 80],
          backgroundColor: purple[200],
          borderColor: purple[500],
          borderWidth: 1,
        },
      ],
    },
    line: {
      labels: ['January', 'February', 'March'],
      datasets: [
        {
          label: 'Revenue',
          data: [85, 69, 90],
          fill: false,
          backgroundColor: purple[100],
          borderColor: purple[500],
        },
      ],
    },
    pie: {
      labels: ['Mouse', 'Keyboard', 'Web Cam'],
      datasets: [
        {
          label: 'Category Sales',
          data: [300, 150, 100],
          backgroundColor: [purple[100], purple[300], purple[500]],
          borderColor: [purple[300], purple[400], purple[700]],
          borderWidth: 1,
        },
      ],
    },
  };

  const handleFilterChange = (filterType, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [filterType]: value,
    }));
  };

  return (
    <ThemeProvider theme={customTheme}>
      <CssBaseline />
      <AppBar position="static" color="primary">
        <Toolbar>
          {isMobile && (
            <IconButton edge="start" color="inherit" onClick={handleDrawerToggle} sx={{ mr: 2 }}>
              <MenuIcon />
            </IconButton>
          )}
          <Box display="flex" alignItems="center" gap={2}>
            <img
              src="/logo.png"
              alt="Chartify Logo"
              style={{ height: 40 }}
            />
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              Chartify
            </Typography>
          </Box>
          {!isMobile && (
            <Box sx={{ display: 'flex', gap: 2 }}>
              {menuItems.map((item) => (
                <Typography key={item} sx={{ cursor: 'pointer' }}>
                  {item}
                </Typography>
              ))}
            </Box>
          )}
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={handleDrawerToggle}
        ModalProps={{ keepMounted: true }}
      >
        {drawer}
      </Drawer>

      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Box mb={3}>
          <Filter onFilterChange={handleFilterChange} />
        </Box>

        <Grid container spacing={3}>
          <Grid item xs={12} sm={12} md={6}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" color="primary" gutterBottom>
                Grafik Penjualan
              </Typography>
              <BarChart data={fakeData.bar} />
            </Paper>
          </Grid>
          <Grid item xs={12} sm={12} md={6}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" color="primary" gutterBottom>
                Grafik Pendapatan
              </Typography>
              <LineChart data={fakeData.line} />
            </Paper>
          </Grid>
          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" color="primary" gutterBottom>
                Distribusi Kategori Penjualan
              </Typography>
              <PieChart data={fakeData.pie} />
            </Paper>
          </Grid>
        </Grid>
      </Container>
    </ThemeProvider>
  );
};

export default App;
