import React, { useState } from 'react';
import { Container, CssBaseline, Grid, Paper, Typography } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import BarChart from './components/BarChart';
import LineChart from './components/LineChart';
import PieChart from './components/PieChart';
import Filter from './components/Filter';

const theme = createTheme();

const App = () => {
  const [filters, setFilters] = useState({ periode: 'monthly', kategori: 'all' });

  const fakeData = {
    bar: {
      labels: ['January', 'February', 'March'],
      datasets: [
        {
          label: 'Sales',
          data: [65, 59, 80],
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
      ],
    },
    line: {
      labels: ['January', 'February', 'March'],
      datasets: [
        {
          label: 'Revenue',
          data: [85, 69, 90],
          fill: false,
          backgroundColor: 'rgba(255, 99, 132, 0.2)',
          borderColor: 'rgba(255, 99, 132, 1)',
        },
      ],
    },
    pie: {
      labels: ['Electronics', 'Fashion', 'Groceries'],
      datasets: [
        {
          label: 'Category Sales',
          data: [300, 150, 100],
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)',
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)',
          ],
          borderWidth: 1,
        },
      ],
    },
  };

  const handleFilterChange = (filterType, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [filterType]: value,
    }));
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" style={{ marginTop: '20px' }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Dashboard Statistik Penjualan
        </Typography>
        <Filter onFilterChange={handleFilterChange} />
        <Grid container spacing={3} style={{ marginTop: '20px' }}>
          <Grid item xs={12} md={6}>
            <Paper style={{ padding: '20px' }}>
              <BarChart data={fakeData.bar} />
            </Paper>
          </Grid>
          <Grid item xs={12} md={6}>
            <Paper style={{ padding: '20px' }}>
              <LineChart data={fakeData.line} />
            </Paper>
          </Grid>
          <Grid item xs={12}>
            <Paper style={{ padding: '20px' }}>
              <PieChart data={fakeData.pie} />
            </Paper>
          </Grid>
        </Grid>
      </Container>
    </ThemeProvider>
  );
};

export default App;
