import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Tentang = () => {
  const [features, setFeatures] = useState([
    'Chart Interaktif',
    'Data Real-Time',
    'Antarmuka yang Mudah Digunakan',
  ]);

  const [technologies, setTechnologies] = useState({
    frontEnd: 'Dibangun menggunakan React.',
    backEnd: 'Menggunakan CodeIgniter 4 (CI4).',
  });

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <motion.h1
        initial={{ x: -100 }}
        animate={{ x: 0 }}
        transition={{ type: 'spring', stiffness: 100 }}
      >
        Tentang Chartify
      </motion.h1>
      
      <motion.h2
        initial={{ y: -20 }}
        animate={{ y: 0 }}
        transition={{ delay: 0.2 }}
      >
        Apa itu Chartify?
      </motion.h2>
      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
      >
        Chartify adalah aplikasi Dashboard Statistik Penjualan yang dirancang untuk menyediakan visualisasi data penjualan yang komprehensif dan interaktif.
      </motion.p>

      <motion.h2
        initial={{ y: -20 }}
        animate={{ y: 0 }}
        transition={{ delay: 0.6 }}
      >
        Tujuan
      </motion.h2>
      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.8 }}
      >
        Aplikasi ini dikembangkan untuk memenuhi penilaian Tugas Besar mata kuliah Mobile Programming Universitas Logistik dan Bisnis Internasional D3TI - 2A.
      </motion.p>

      <motion.h2
        initial={{ y: -20 }}
        animate={{ y: 0 }}
        transition={{ delay: 1 }}
      >
        Teknologi yang Digunakan
      </motion.h2>
      <motion.ul
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.2 }}
      >
        <li><strong>Front-End</strong>: {technologies.frontEnd}</li>
        <li><strong>Back-End</strong>: {technologies.backEnd}</li>
      </motion.ul>

      <motion.h2
        initial={{ y: -20 }}
        animate={{ y: 0 }}
        transition={{ delay: 1.4 }}
      >
        Fitur
      </motion.h2>
      <motion.ul
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.6 }}
      >
        {features.map((feature, index) => (
          <li key={index}>{feature}</li>
        ))}
      </motion.ul>
    </motion.div>
  );
};

export default Tentang;
