import React from 'react';
import { CssBaseline, TextField, Button, Typography, Container, Box, Paper, Grid } from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { blue } from '@mui/material/colors';

const theme = createTheme({
  palette: {
    primary: {
      main: blue[500],
    },
  },
  typography: {
    fontFamily: 'Roboto, sans-serif',
  },
});

const Login = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          display: 'flex',
          minHeight: '100vh',
          backgroundColor: '#f0f2f5',
        }}
      >
        <Grid container>
          <Grid
            item
            xs={12}
            md={6}
            sx={{
              background: 'linear-gradient(to bottom right, #00c6ff, #0072ff)',
              color: 'white',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              padding: 8,
              height: '100vh',
            }}
          >
            <Typography variant="h4" gutterBottom>
              Single sign-on (SSO)
            </Typography>
            <Typography variant="h5" gutterBottom>
              WELCOME BACK!
            </Typography>
            <Typography variant="body1" align="center">
              Enter your ID and Password to continue
            </Typography>
          </Grid>
          <Grid
            item
            xs={12}
            md={6}
            sx={{
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              padding: 8,
              height: '100vh',
            }}
          >
            <Paper elevation={3} sx={{ padding: 4, width: '70%', maxWidth: 400 }}>
              <Typography variant="h5" align="center" gutterBottom>
                SIGN IN
              </Typography>
              <Typography variant="subtitle1" align="center" gutterBottom>
                TO ACCESS THE PORTAL
              </Typography>
              <Box component="form" sx={{ mt: 1 }}>
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="username"
                  label="Enter User Name Here"
                  name="username"
                  autoComplete="username"
                  autoFocus
                  InputProps={{
                    startAdornment: <i className="fas fa-user" />,
                  }}
                />
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="password"
                  label="Enter Password"
                  type="password"
                  id="password"
                  autoComplete="current-password"
                  InputProps={{
                    startAdornment: <i className="fas fa-key" />,
                  }}
                />
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  color="primary"
                  sx={{ mt: 3, mb: 2 }}
                >
                  Login
                </Button>
                <Typography variant="body2" align="center">
                  <a href="#" style={{ textDecoration: 'none', color: theme.palette.primary.main }}>
                    Forgot Password?
                  </a>
                </Typography>
              </Box>
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider>
  );
};

export default Login;
