import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Ganti useHistory dengan useNavigate
import { TextField, Button, Box, Typography } from '@mui/material';

const Login = () => {
  const [username, setUsername] = useState(''); // Definisikan username
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Gunakan useNavigate

  const handleLogin = () => {
    // Logika otentikasi
    if (username === 'admin' && password === 'password') {
      localStorage.setItem('token', 'your-token'); // Simpan token ke localStorage
      navigate('/'); // Arahkan ke halaman dashboard setelah login
    } else {
      alert('Username atau password salah!');
    }
  };

  return (
    <Box display="flex" flexDirection="column" alignItems="center" justifyContent="center" minHeight="100vh">
      <Typography variant="h4" gutterBottom>
        Login
      </Typography>
      <TextField
        label="Username"
        variant="outlined"
        margin="normal"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        fullWidth
      />
      <TextField
        label="Password"
        type="password"
        variant="outlined"
        margin="normal"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        fullWidth
      />
      <Button variant="contained" color="primary" onClick={handleLogin}>
        Login
      </Button>
    </Box>
  );
};

export default Login;
