import React, { useState } from 'react';
import {
  Container,
  CssBaseline,
  Grid,
  Paper,
  Typography,
  Box
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { purple } from '@mui/material/colors';
import BarChart from './components/BarChart';
import LineChart from './components/LineChart';
import PieChart from './components/PieChart';
import Filter from './components/Filter';

// Tema ungu
const theme = createTheme({
  palette: {
    primary: {
      main: purple[500],
    },
    secondary: {
      main: purple[200],
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: 'Roboto, sans-serif',
    h4: {
      fontWeight: 600,
    },
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: '16px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        },
      },
    },
  },
});

const App = () => {
  const [filters, setFilters] = useState({ periode: 'monthly', kategori: 'all' });

  const fakeData = {
    bar: {
      labels: ['January', 'February', 'March'],
      datasets: [
        {
          label: 'Sales',
          data: [65, 59, 80],
          backgroundColor: purple[200],
          borderColor: purple[500],
          borderWidth: 1,
        },
      ],
    },
    line: {
      labels: ['January', 'February', 'March'],
      datasets: [
        {
          label: 'Revenue',
          data: [85, 69, 90],
          fill: false,
          backgroundColor: purple[100],
          borderColor: purple[500],
        },
      ],
    },
    pie: {
      labels: ['Electronics', 'Fashion', 'Groceries'],
      datasets: [
        {
          label: 'Category Sales',
          data: [300, 150, 100],
          backgroundColor: [
            purple[100],
            purple[300],
            purple[500],
          ],
          borderColor: [
            purple[300],
            purple[400],
            purple[700],
          ],
          borderWidth: 1,
        },
      ],
    },
  };

  const handleFilterChange = (filterType, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [filterType]: value,
    }));
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom color="primary">
          Dashboard Statistik Penjualan
        </Typography>

        <Box mb={3}>
          <Filter onFilterChange={handleFilterChange} />
        </Box>

        <Grid container spacing={3}>
          <Grid item xs={12} sm={12} md={6}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" color="primary" gutterBottom>
                Grafik Penjualan
              </Typography>
              <BarChart data={fakeData.bar} />
            </Paper>
          </Grid>
          <Grid item xs={12} sm={12} md={6}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" color="primary" gutterBottom>
                Grafik Pendapatan
              </Typography>
              <LineChart data={fakeData.line} />
            </Paper>
          </Grid>
          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" color="primary" gutterBottom>
                Distribusi Kategori Penjualan
              </Typography>
              <PieChart data={fakeData.pie} />
            </Paper>
          </Grid>
        </Grid>

      </Container>
    </ThemeProvider>
  );
};

export default App;
